        console.log($widget)
        return $widget
      } catch (e) {
        console.log(e)
      }
    }
    getData()
  }, [])

  return (
    <div>
      <img src={widget.config ? widget.config.url : ''} />
    </div>
  )
}

export default ImageWidget
