import { useState, useEffect } from 'react'
import { makeStyles } from '@material-ui/core/styles'
import Grid from '@material-ui/core/Grid'
import Card from '@material-ui/core/Card'
import Button from '@material-ui/core/Button'
import InputLabel from '@material-ui/core/InputLabel'
import Select from '@material-ui/core/Select'
import MenuItem from '@material-ui/core/MenuItem'
import FormControl from '@material-ui/core/FormControl'
import FormControlLabel from '@material-ui/core/FormControlLabel'
import Typography from '@material-ui/core/Typography'
import ArrowUpwardIcon from '@material-ui/icons/ArrowUpward'
import ArrowDownwardIcon from '@material-ui/icons/ArrowDownward'
import WidgetSettingsActions from '../../../actions/widgetSettings.actions'
import { conditionCases } from '../../../helpers/measure-conditions'

const useStyles = makeStyles({

})

const TableMeasureConditions = ({ widget, handleSettingsClose, isAdding, onWidgetAdd }) => {
  const classes = useStyles()
  const [measureConditionsFields, updateMeasureConditionsFields] = useState({
    expression: null, minValue: null, maxValue: null, field: null
  })
  const [fields, setFields] = useState(widget.config.fields)
  const [measureConditions, setMeasureConditions] = useState([])
  const widgetId = widget.id

  useEffect(() => {
    const getMeasureConditions = async () => {
      try {
        const $measureConditions = await WidgetSettingsActions.getWidgetSettingsMeasureConditions(widgetId)
        console.log($measureConditions)
        setMeasureConditions($measureConditions)
      } catch (e) {
        console.log(e)
      }
    }
    getMeasureConditions()
    console.log(fields)
  }, [])

  const addMeasureCondition = async () => {
    try {
      const measureCondition = await WidgetSettingsActions.createWidgetSettingsMeasureConditions(
        widgetId,
        {}
      )
      console.log(measureCondition)
    } catch (e) {
      console.log(e)
    }
  }

  const removeMeasureCondition = async (measureConditionId) => {
    try {
      const measureCondition = await WidgetSettingsActions.deleteWidgetSettingsMeasureConditions(widgetId, measureConditionId)
      console.log(measureCondition)
    } catch (e) {
      console.log(e)
    }
  }

  const renderMeasureCondition = (measureCondition) => (
    <Card>
      <Grid container spacing={0}>
        <Grid item xs={4}>
          <Typography>Field</Typography>
          <Typography>{measureCondition.field}</Typography>
        </Grid>
        <Grid item xs={4}>
          <Typography>Field</Typography>
          <Typography>{measureCondition.expression}</Typography>
        </Grid>
        <Grid item xs={4}>
          <Typography>Color</Typography>
          <Typography>{measureCondition.color}</Typography>
        </Grid>
      </Grid>
      <Grid container spacing={0}>
        <Grid item xs={6}>
          <ArrowDownwardIcon />
          <span>{measureCondition.maxValue}</span>
        </Grid>
        <Grid xs={6}>
          <ArrowUpwardIcon />
          <span>{measureCondition.minValue}</span>
        </Grid>
      </Grid>
    </Card>
  )

  return (
    <div>

    </div>
  )
}

export default TableMeasureConditions
