import { useState, useEffect } from 'react'
import { makeStyles } from '@material-ui/core/styles'
import DashboardActions from '../../../actions/dashboard.actions'
import Typography from '@material-ui/core/Typography'
import TextField from '@material-ui/core/TextField'
import Button from '@material-ui/core/Button'

const useStyles = makeStyles(() => ({
  h2: {
    width: '100%',
    textAlign: 'center',
    borderBottom: '1px solid #000',
    lineHeight: '0.1em',
    marginTop: "20px",
    marginBottom: "20px",
    fontFamily: 'Roboto',
    fontSize: '13px',
    color: "#3F51B5"
  },
  span: {
    background: '#fff',
    padding: '0 10px'
  }
}))

const GeneralSettings = ({ id }) => {
  const classes = useStyles()
  const [state, setState] = useState({ title: '', description: '' })
  const [showDelete, setShowDelete] = useState(false)
  const [dashboard, setDashboard] = useState({})

  useEffect(() => {
    const getInitialProps = async () => {
      try {
        const $dashboard = await DashboardActions.getDashboardById({ id })
        setDashboard($dashboard)
        setState({ ...state, title: $dashboard.title, description: dashboard.description })
      } catch (e) {
        console.log(e)
      }
    }
    getInitialProps()
  }, [])

  return (
    <div>
      <h2 className={classes.h2}>
        <span className={classes.span}>Dashboard Details</span>
      </h2>
      <div>
        <TextField label="Title" value={state.title} placeholder="Dashboard Title" fullWidth />

        <TextField
          style={{ marginTop: 10 }}
          fullWidth
          value={state.description}
          label="Description"
          placeholder="Dashboard Description"
          multiline
          rows={2}
          rowsMax={4}
        />

        <Button fullWidth style={{ marginTop: 10 }}>Save Changes</Button>
      </div>

      <h2 className={classes.h2}>
        <span className={classes.span}>Delete Project</span>
      </h2>
      <Button fullWidth style={{ marginTop: 10, backgroundColor: '#c0392b', color: "#FFF" }}>Delete Project</Button>

    </div>
  )
}

export default GeneralSettings
