import { NextPage } from 'next'
import Cookies from 'next-cookies'
import dynamic from 'next/dynamic'
import WidgetSettingsActions from '../../actions/widgetSettings.actions'

const Gantt = dynamic(() => import('../../components/widgets/gantt-chart-widget'), {
  ssr: false
});

const data = {
  data: [
    { id: 1, text: 'Task #1', start_date: '15-04-2019', duration: 3, progress: 0.6 },
    { id: 2, text: 'Task #2', start_date: '18-04-2019', duration: 3, progress: 0.4 }
  ],
  links: [
    { id: 1, source: 1, target: 2, type: '0' }
  ]
}

const Widgets: NextPage<{ WidgetSettings: any }> = ({ WidgetSettings }) => (
  <div>
    <div className="gantt-container" style={{ height: '100vh' }}>
      <Gantt tasks={data} />
    </div>
  </div>
)

Widgets.getInitialProps = async (req) => {
  const Authorization = Cookies(req).id
  const WidgetSettings = await WidgetSettingsActions.getWidgetSettingsById("5e78a808f2c42d5a58cc2cdc", Authorization)
  return { WidgetSettings }
}

export default Widgets
