import { useState, useEffect } from 'react'
import { makeStyles } from '@material-ui/core/styles'
import Typography from '@material-ui/core/Typography'
import DashboardActions from '../../../actions/dashboard.actions'
import Grid from '@material-ui/core/Grid'
import DeleteIcon from '@material-ui/icons/Delete'
import IconButton from '@material-ui/core/IconButton'
import FormControlLabel from '@material-ui/core/FormControlLabel'
import Checkbox from '@material-ui/core/Checkbox'
import Card from '@material-ui/core/Card'

const useStyles = makeStyles(() => ({
  h2: {
    width: '100%',
    textAlign: 'center',
    borderBottom: '1px solid #000',
    lineHeight: '0.1em',
    marginTop: "20px",
    marginBottom: "20px",
    fontFamily: 'Roboto',
    fontSize: '13px',
    color: "#3F51B5"
  },
  span: {
    background: '#fff',
    padding: '0 10px'
  },
  name: {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center'
  },
  collaboratorActionButtons: {
    display: 'flex'
  }
}))

const PrivacySettings = ({ id }) => {
  const classes = useStyles()
  const [collaborators, setCollaborators] = useState([])

  useEffect(() => {
    const getInitialProps = async () => {
      try {
        const $collaborators = await DashboardActions.getCollaborators({ id })
        console.log($collaborators)
        setCollaborators($collaborators)
      } catch (e) {
        console.log(e)
      }
    }
    getInitialProps()
  }, [])

  const renderCollaborator = ({ firstName, lastName, email }) => (
    <Card style={{ padding: '0 15px' }}>
      <Grid container style={{ margin: 0, width: '100%' }}>
        <Grid item xs={4} className={classes.name}>
          <Typography>email@domain.com</Typography>
          <Typography>Username</Typography>
        </Grid>
        <Grid item xs={4}>
          <FormControlLabel
            style={{ margin: 0 }}
            control={<Checkbox name="gilad" />}
            label="Editor"
          />
          <FormControlLabel
            style={{ margin: 0 }}
            control={<Checkbox name="gilad" />}
            label="Viewer"
          />
        </Grid>
        <Grid item xs={4} className={classes.collaboratorActionButtons}>
          <IconButton aria-label="delete" className={classes.margin}>
            <DeleteIcon fontSize="small" />
          </IconButton>
        </Grid>
      </Grid>
    </Card>
  )

  return (
    <div>
      <h2 className={classes.h2}>
        <span className={classes.span}>Collaborators</span>
      </h2>
      <div>
        {renderCollaborator({})}
      </div>
    </div>
  )
}

export default PrivacySettings
