import * as React from 'react';
import Paper from '@material-ui/core/Paper';
import {
  Chart,
  ArgumentAxis,
  ValueAxis,
  BarSeries,
  LineSeries,
  Legend,
} from '@devexpress/dx-react-chart-material-ui';
import { ValueScale } from '@devexpress/dx-react-chart';

export default class Demo extends React.PureComponent {
  constructor(props) {
    super(props);

    this.state = {
      data: [
        { month: 'Jan', total: 987 },
        { month: 'Feb', total: 3000 },
        { month: 'March', total: 1100 },
        { month: 'April', total: 7100 },
        { month: 'May', total: 4300 },
        { month: 'June', total: 7500 },
      ]
    }
  }

  render() {
    const { data: chartData } = this.state;

    return (
      <Paper>
        <Chart
          data={chartData}
        >
          <ValueScale name="total" />

          <ArgumentAxis />
          <ValueAxis scaleName="total" position="left" showGrid={true} showLine showTicks />

          <LineSeries
            name="Total Transactions"
            valueField="total"
            argumentField="month"
            scaleName="total"
          />

          <Legend />
        </Chart>
      </Paper>
    );
  }
}
