import { useState } from 'react'
import { NextPage } from 'next'
import { makeStyles } from '@material-ui/core/styles'
import GridLayout from 'react-grid-layout'
import MaterialTable from 'material-table'
import IconButton from '@material-ui/core/IconButton'
import Button from '@material-ui/core/Button'
import Card from '@material-ui/core/Card'
import ArrowLeft from '@material-ui/icons/ArrowLeft'
import SaveIcon from '@material-ui/icons/Save'
import DashboardIcon from '@material-ui/icons/Dashboard'
import Drawer from '@material-ui/core/Drawer'
import WidgetSidemenu from '../../../components/dashboards/system-widgets'

const useStyles = makeStyles({
  list: {
    width: 250,
  },
  fullList: {
    width: 'auto',
  }
})

const EditDashboard: NextPage<{ userAgent: string }> = ({ userAgent }) => {
  const classes = useStyles()
  const [menuState, setMenuState] = useState({ right: false })
  const [layout, setLayout] = useState([
    { i: '0', x: 0, y: 0, w: 6, h: 11, minW: 6, maxW: 11 }
  ])

  const onLayoutChange = (e) => {
    console.log(e)
  }

  const toggleDrawer = (side, open) => event => {
    if (event.type === 'keydown' && (event.key === 'Tab' || event.key === 'Shift'))
      return

    setMenuState({ ...menuState, [side]: open });
  }

  const addChart = () => {
    const lastElementIndex: number = layout.length
    setLayout([...layout, { i: `${lastElementIndex}`, x: 0, y: 0, w: 6, h: 11, minW: 6, maxW: 11 }])
  }

  const sideList = side => (
    <div
      className={classes.list}
      role="presentation"
      onClick={toggleDrawer(side, false)}
      onKeyDown={toggleDrawer(side, false)}
    >
      {/* <WidgetSidemenu /> */}
    </div>
  )

  const renderWidgets = () => layout.map(layout => (
    <div key={layout.i}>
      <MaterialTable
        columns={[
          { title: 'Adı', field: 'name' },
          { title: 'Soyadı', field: 'surname' },
          { title: 'Doğum Yılı', field: 'birthYear', type: 'numeric' },
          { title: 'Doğum Yeri', field: 'birthCity', lookup: { 34: 'İstanbul', 63: 'Şanlıurfa' } }
        ]}
        data={[{ name: 'Mehmet', surname: 'Baran', birthYear: 1987, birthCity: 63 }]}
        title="Demo Title"
      />
    </div>
  ))

  return (
    <div>
      <Drawer anchor="right" open={menuState.right} onClose={toggleDrawer('right', false)}>
        {sideList('right')}
      </Drawer>

      <Card style={{ display: 'flex', flexDirection: 'row' }}>
        <div style={{ justifySelf: 'flex-start', display: 'flex', flex: 1 }}>
          <IconButton aria-label="back" size="medium" style={{ outline: 'none' }}>
            <ArrowLeft fontSize="default" />
          </IconButton>
        </div>
        <div style={{ justifySelf: 'flex-end', display: 'flex', flex: 1, justifyContent: 'flex-end', paddingRight: '10px' }}>
          <Button
            style={{ outline: 'none' }}
            size="small"
            startIcon={<SaveIcon />}>
            Save
          </Button>
          <Button
            style={{ marginLeft: '10px', outline: 'none' }}
            size="small"
            onClick={toggleDrawer('right', true)}
            startIcon={<DashboardIcon />}>
            Widgets
          </Button>
        </div>
      </Card>

      <GridLayout onLayoutChange={onLayoutChange} className="layout" layout={layout} cols={16} rowHeight={30} width={1920}>
        {renderWidgets()}
      </GridLayout>
    </div>
  )
};

export default EditDashboard;
