import { NextPage } from 'next';
import Gantt from '../../components/widgets/gantt-chart-widget'
import dynamic from 'next/dynamic'

const data = {
  data: [
    { id: 1, text: 'Task #1', start_date: '15-04-2019', duration: 3, progress: 0.6 },
    { id: 2, text: 'Task #2', start_date: '18-04-2019', duration: 3, progress: 0.4 }
  ],
  links: [
    { id: 1, source: 1, target: 2, type: '0' }
  ]
}

const Widgets: NextPage<{ userAgent: string }> = ({ userAgent }) => (
  <div>
    <div className="gantt-container">
      <Gantt tasks={data} />
    </div>
  </div>
)

Widgets.getInitialProps = async ({ req }) => {
  const userAgent = req ? req.headers['user-agent'] || '' : navigator.userAgent
  return { userAgent }
}

export default Widgets
