import { useEffect, useState, Fragment } from "react";
import Router from "next/router";
import { makeStyles } from "@material-ui/core/styles";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemText from "@material-ui/core/ListItemText";
import Divider from "@material-ui/core/Divider";
import WebIcon from "@material-ui/icons/Web";
import DashboardIcon from "@material-ui/icons/Dashboard";
import StorageIcon from "@material-ui/icons/Storage";
import AssessmentIcon from "@material-ui/icons/Assessment";
import DashboardActions from "../actions/dashboard.actions";

import ExpandLess from '@material-ui/icons/ExpandLess';
import ExpandMore from '@material-ui/icons/ExpandMore';
import StarBorder from '@material-ui/icons/StarBorder';
import Collapse from '@material-ui/core/Collapse';

const useStyles = makeStyles((theme) => ({
  list: {
    width: 250,
  },
  fullList: {
    width: "auto",
  },
  nested: {
    paddingLeft: theme.spacing(4),
  },
}));

const SideMenu = () => {
  const classes = useStyles();
  const [dashboards, setDashboards] = useState([]);
  const [webPages, setWebPages] = useState([]);
  const [refresh, setRefresh] = useState(0);
  const [routes, setRoutes] = useState([
    {
      key: "dashboards",
      text: "Dashboards",
      route: "/dashboards",
      icon: () => <DashboardIcon />,
      list: dashboards,
      open: false,
    },
    {
      key: "datasources",
      text: "Data Sources",
      route: "/datasources",
      icon: () => <StorageIcon />,
    },
    {
      key: "webpages",
      text: "Web Pages",
      route: "/webpages",
      icon: () => <WebIcon />,
      list: webPages,
      open: false,
    },
    {
      key: "widgets",
      text: "Widgets",
      route: "/widgets",
      icon: () => <AssessmentIcon />,
    },
  ]);

  useEffect(() => {
    const udpateView = async () => {
      try {
        const GetDashboards = await DashboardActions.getDashboards({});
        const GetWebPages = await DashboardActions.getWebpages({});
        setDashboards(GetDashboards);
        setWebPages(GetWebPages);
      } catch (e) {
        console.log(e);
      }
    };
    udpateView();
  }, [refresh]);

  return (
    <div className={classes.list} role="presentation">
      <List>
        {routes.map((item, index) =>
          item.list ? (
            <Fragment key={item.key}>

              <ListItem
                button
                onClick={() => {
                  setRoutes([
                    ...routes,
                    (routes.find((e) => e.title == item.title).open = true),
                  ]);
                }}
              >
                {/* <ListItemIcon>{item.icon()}</ListItemIcon> */}
                <ListItemText primary={item.text} />
                {item.open ? <ExpandLess /> : <ExpandMore />}
              </ListItem>

              <Collapse in={item.open} timeout="auto" unmountOnExit>
                <List component="div" disablePadding>
                  <ListItem button className={classes.nested}>
                    <ListItemIcon>
                      <StarBorder />
                    </ListItemIcon>
                    <ListItemText primary="Starred" />
                  </ListItem>
                </List>
              </Collapse>

            </Fragment>
          ) : (
            <ListItem
              button
              key={item.key}
              onClick={() => {
                Router.push(item.route);
              }}
            >
              <ListItemIcon>{item.icon()}</ListItemIcon>
              <ListItemText primary={item.text} />
            </ListItem>
          )
        )}
      </List>
    </div>
  );
};

export default SideMenu;
