import axios from 'axios';
import config from 'next/config';
import Cookie from 'js-cookie';
const {
  API_URL
} = config().publicRuntimeConfig;
export default class NotificationsActions {
  static async getMyNotificationsCount({
    Authorization,
    userId
  }) {
    return axios.get(`${API_URL}/users/${userId || Cookie.get('userId')}/notifications/count`, {
      headers: {
        Authorization: Authorization || Cookie.get('id')
      },
      params: {
        filter: {
          where: {
            seen: false
          }
        }
      }
    }).then(res => res.data).catch(err => err);
  }

  static async getMyNotifications({
    Authorization,
    userId
  }) {
    return axios.get(`${API_URL}/users/${userId || Cookie.get('userId')}/notifications`, {
      headers: {
        Authorization: Authorization || Cookie.get('id')
      }
    }).then(res => res.data).catch(err => err);
  }

  static async markAsSeen({
    Authorization,
    id
  }) {
    return axios.patch(`${API_URL}/Notifications/${id}`, {
      seen: true
    }, {
      headers: {
        Authorization: Authorization || Cookie.get('id')
      }
    }).then(res => res.data).catch(err => err);
  }

}
