import { useEffect, useState } from 'react'
import { makeStyles } from '@material-ui/core/styles'
import Card from '@material-ui/core/Card'
import Grid from '@material-ui/core/Grid'
import NotificationsActions from '../../actions/notifications.actions'
import MenuItem from '@material-ui/core/MenuItem'
import Menu from '@material-ui/core/Menu'
import MenuIcon from '@material-ui/icons/Menu'
import NotificationItem from './NotificationsItem'

const useStyles = makeStyles(theme => ({
  gridItem: {

  }
}))

const NotificationMenu = ({ anchorEl, style, open, onClose }) => {
  const classes = useStyles()
  const [notifications, setNotifications] = useState([])

  useEffect(() => {
    const $getNotifications = async () => {
      try {
        const $notifications = await NotificationsActions.getMyNotifications({})
        console.log($notifications)
        setNotifications([...$notifications])
      } catch (e) {
        console.log(e)
      }
    }
    $getNotifications()
  }, [])

  const renderNotificationItem = (notification) => (
    <MenuItem key={notification.id} style={{ padding: 0 }}>
      <NotificationItem notification={notification} />
    </MenuItem>
  )

  return (
    <Menu
      anchorEl={anchorEl}
      keepMounted
      style={style}
      open={open}
      onClose={onClose}
    >
      {notifications.map(notification => renderNotificationItem(notification))}
    </Menu>
  )
}

export default NotificationMenu
