import { useState, useEffect } from 'react'
import { makeStyles } from '@material-ui/core/styles'
import Grid from '@material-ui/core/Grid'
import Card from '@material-ui/core/Card'
import Button from '@material-ui/core/Button'
import InputLabel from '@material-ui/core/InputLabel'
import Select from '@material-ui/core/Select'
import MenuItem from '@material-ui/core/MenuItem'
import FormControl from '@material-ui/core/FormControl'
import FormControlLabel from '@material-ui/core/FormControlLabel'
import Typography from '@material-ui/core/Typography'
import ArrowUpwardIcon from '@material-ui/icons/ArrowUpward'
import ArrowDownwardIcon from '@material-ui/icons/ArrowDownward'
import EditIcon from '@material-ui/icons/Edit'
import DeleteIcon from '@material-ui/icons/Delete'
import WidgetSettingsActions from '../../../actions/widgetSettings.actions'
import { conditionCases } from '../../../helpers/measure-conditions'

const useStyles = makeStyles({
  h2: {
    width: '100%',
    textAlign: 'center',
    borderBottom: '1px solid #000',
    lineHeight: '0.1em',
    marginTop: "20px",
    marginBottom: "20px",
    fontFamily: 'Roboto',
    fontSize: '13px',
    color: "#3F51B5"
  },
  span: {
    background: '#fff',
    padding: '0 10px'
  },
  measureConditionFieldTitle: {
    fontSize: 12,
    fontWeight: 'bold'
  }
})

const TableMeasureConditions = ({ widget, handleSettingsClose, isAdding, onWidgetAdd }) => {
  const classes = useStyles()
  const [measureConditionsFields, updateMeasureConditionsFields] = useState({
    expression: null, minValue: null, maxValue: null, field: null
  })
  const [fields, setFields] = useState(widget.config.fields)
  const [measureConditions, setMeasureConditions] = useState([])
  const widgetId = widget.id

  useEffect(() => {
    const getMeasureConditions = async () => {
      try {
        const $measureConditions = await WidgetSettingsActions.getWidgetSettingsMeasureConditions(widgetId)
        console.log($measureConditions)
        setMeasureConditions($measureConditions)
      } catch (e) {
        console.log(e)
      }
    }
    getMeasureConditions()
    console.log(fields)
  }, [])

  const addMeasureCondition = async () => {
    try {
      const measureCondition = await WidgetSettingsActions.createWidgetSettingsMeasureConditions(
        widgetId,
        {}
      )
      console.log(measureCondition)
    } catch (e) {
      console.log(e)
    }
  }

  const removeMeasureCondition = async (measureConditionId) => {
    try {
      const measureCondition = await WidgetSettingsActions.deleteWidgetSettingsMeasureConditions(widgetId, measureConditionId)
      console.log(measureCondition)
    } catch (e) {
      console.log(e)
    }
  }

  const renderMeasureCondition = (measureCondition) => (
    <Card style={{ padding: 15, marginBottom: 15 }}>
      <Grid container spacing={0}>
        <Grid item xs={4}>
          <Typography className={classes.measureConditionFieldTitle}>Field</Typography>
          <Typography>{measureCondition.field}</Typography>
        </Grid>
        <Grid item xs={4}>
          <Typography className={classes.measureConditionFieldTitle}>Expression</Typography>
          <Typography>{measureCondition.expression}</Typography>
        </Grid>
        <Grid item xs={4}>
          <Typography className={classes.measureConditionFieldTitle}>Color</Typography>
          <Typography>{measureCondition.color}</Typography>
        </Grid>
      </Grid>
      <Grid container spacing={0} style={{ marginTop: 15 }}>
        <Grid item xs={6} style={{ display: 'flex', justifyContent: 'center', alignContent: 'center', alignItems: 'center' }}>
          <ArrowUpwardIcon />
          <span>{measureCondition.minValue}</span>
        </Grid>
        <Grid item xs={6} style={{ display: 'flex', justifyContent: 'center', alignContent: 'center', alignItems: 'center' }}>
          <ArrowDownwardIcon />
          <span>{measureCondition.maxValue}</span>
        </Grid>
      </Grid>
      <Grid container spacing={0} style={{ marginTop: 15 }}>
        <Grid item xs={6}>
          <Button fullWidth>
            <EditIcon />
          </Button>
        </Grid>
        <Grid item xs={6}>
          <Button fullWidth>
            <DeleteIcon />
          </Button>
        </Grid>
      </Grid>
    </Card>
  )

  return (
    <div>
      <h2 className={classes.h2}>
        <span className={classes.span}>Measure Conditions</span>
      </h2>
      {
        measureConditions.map(measureCondition => renderMeasureCondition(measureCondition))
      }
    </div>
  )
}

export default TableMeasureConditions
