import { useState } from 'react'
import { NextPage } from 'next'
import { makeStyles } from '@material-ui/core/styles'
import GridLayout from 'react-grid-layout'
import MaterialTable from 'material-table'
import IconButton from '@material-ui/core/IconButton'
import Button from '@material-ui/core/Button'
import Card from '@material-ui/core/Card'
import ArrowLeft from '@material-ui/icons/ArrowLeft'
import SaveIcon from '@material-ui/icons/Save'
import DashboardIcon from '@material-ui/icons/Dashboard'

import Drawer from '@material-ui/core/Drawer'
import List from '@material-ui/core/List'
import Divider from '@material-ui/core/Divider'
import ListItem from '@material-ui/core/ListItem'
import ListItemIcon from '@material-ui/core/ListItemIcon'
import ListItemText from '@material-ui/core/ListItemText'
import InboxIcon from '@material-ui/icons/MoveToInbox'
import MailIcon from '@material-ui/icons/Mail'

const useStyles = makeStyles({
  list: {
    width: 250,
  },
  fullList: {
    width: 'auto',
  },
})

const EditDashboard: NextPage<{ userAgent: string }> = ({ userAgent }) => {
  const classes = useStyles()
  const [openWidgetsMenu, setOpenWidgetsMenu] = useState(false)

  const layout = [
    { i: 'a', x: 0, y: 0, w: 1, h: 2, static: true },
    { i: 'b', x: 0, y: 0, w: 3, h: 2, minW: 2, maxW: 4 },
    { i: 'c', x: 0, y: 0, w: 6, h: 11, minW: 6, mingH: 11 }
  ]

  const onLayoutChange = (e) => {
    console.log(e)
  }

  const toggleDrawer = (open: boolean) => event => {
    if (event.type === 'keydown' && (event.key === 'Tab' || event.key === 'Shift'))
      return
    setOpenWidgetsMenu(open)
  }

  const sideList = side => (
    <div
      className={classes.list}
      role="presentation"
      onClick={toggleDrawer(false)}
      onKeyDown={toggleDrawer(false)}
    >
      <List>
        {['Inbox', 'Starred', 'Send email', 'Drafts'].map((text, index) => (
          <ListItem button key={text}>
            <ListItemIcon>{index % 2 === 0 ? <InboxIcon /> : <MailIcon />}</ListItemIcon>
            <ListItemText primary={text} />
          </ListItem>
        ))}
      </List>
      <Divider />
      <List>
        {['All mail', 'Trash', 'Spam'].map((text, index) => (
          <ListItem button key={text}>
            <ListItemIcon>{index % 2 === 0 ? <InboxIcon /> : <MailIcon />}</ListItemIcon>
            <ListItemText primary={text} />
          </ListItem>
        ))}
      </List>
    </div>
  )

  return (
    <div>
      <Drawer open={openWidgetsMenu} onClose={toggleDrawer(false)}>
        {sideList('right')}
      </Drawer>

      <Card style={{ display: 'flex', flexDirection: 'row' }}>
        <div style={{ justifySelf: 'flex-start', display: 'flex', flex: 1 }}>
          <IconButton aria-label="back" size="medium" style={{ outline: 'none' }}>
            <ArrowLeft fontSize="default" />
          </IconButton>
        </div>
        <div style={{ justifySelf: 'flex-end', display: 'flex', flex: 1, justifyContent: 'flex-end', paddingRight: '10px' }}>
          <Button
            style={{ outline: 'none' }}
            size="small"
            startIcon={<SaveIcon />}>
            Save
          </Button>
          <Button
            style={{ marginLeft: '10px', outline: 'none' }}
            size="small"
            onClick={toggleDrawer(true)}
            startIcon={<DashboardIcon />}>
            Widgets
          </Button>
        </div>
      </Card>

      <GridLayout onLayoutChange={onLayoutChange} className="layout" layout={layout} cols={16} rowHeight={30} width={1920}>
        <div key="a" style={{ backgroundColor: '#00F' }}>a</div>
        <div key="b" style={{ backgroundColor: '#0F0' }}>b</div>
        <div key="c">
          <MaterialTable
            columns={[
              { title: 'Adı', field: 'name' },
              { title: 'Soyadı', field: 'surname' },
              { title: 'Doğum Yılı', field: 'birthYear', type: 'numeric' },
              { title: 'Doğum Yeri', field: 'birthCity', lookup: { 34: 'İstanbul', 63: 'Şanlıurfa' } }
            ]}
            data={[{ name: 'Mehmet', surname: 'Baran', birthYear: 1987, birthCity: 63 }]}
            title="Demo Title"
          />
        </div>
      </GridLayout>
    </div>
  )
};

export default EditDashboard;
