/**
 * Adds loadHtmltemplate command
 */
import { loadHtmltemplate } from "./consts";
export default (function (editor, config) {
  var cm = editor.Commands;
  cm.add(loadHtmltemplate, function (e) {
    var pHtml = e.getModel().get('Parser').parserHtml; // JSX expression in attributes are quoted, so unquote them before passing on

    var html = pHtml.unquoteJsxExpresionsInAttributes(e.getHtml());
    config.setHtmlString(html);
    config.setCssString(e.getCss());
  });
  cm.add('save-db', {
    run: function run(editor, sender) {
      sender && sender.set('active');
      editor.store();
    }
  });
});
